import {Component, OnInit} from '@angular/core';
import {Test} from "../../../model/Test";
import {Account} from "../../../model/Account";
import {TestService} from "../../../service/test-service";

@Component({
  selector: 'app-user-test-list-view',
  templateUrl: './userTestListView.html',
  styleUrls: ['./userTestListView.css']
})
export class UserTestListView implements OnInit
{
   testList:Test[] = new Array();
   user:Account = new Account();
   constructor(private testService:TestService,)
   {}

  ngOnInit(): void
  {
    // @ts-ignore
    this.user = JSON.parse( window.sessionStorage.getItem('MCQuser') );
    this.testService.getAllTest()
      .subscribe(
        data =>
        {
          this.testList = data

        }
      )
  }

   showTest(test:Test)
   {
     window.location.href = "user/myTest/" + test.id;
   }

}
<div class="container mt-4">
  <div class="row text-center pt-4">
    <h4 class="titleUnderLine">TEST</h4>
  </div>
  <div class="row mt-5 mb-5">
      <div class="col-3 mt-4" *ngFor="let test of testList">
        <div class="setCard" *ngIf="test.level <= user.level" (click)="showTest(test)">
          <div class="setCard-title text-secondary">
            <h5>{{test.topic}}</h5>
          </div>
          <div class="setCard-body p-4 text-center" style="height: 100px">
              <h3>{{test.name}}</h3>
          </div>
          <div class="setCard-footer text-secondary text-end">
            <h6>{{test.createTime}}</h6>
          </div>
        </div>

        <div class="lockedCard lockedSet" *ngIf="test.level > user.level">
          <div class="lockedCard-title text-secondary">
            <h5>{{test.topic}}</h5>
          </div>
          <div class="lockedCard-body p-4 text-center" style="height: 100px">
            <h3>{{test.name}}</h3>
            <div class="lockedText"><i class="bi bi-lock"></i></div>
          </div>
          <div class="lockedCard-footer text-secondary text-end">
            <h6>{{test.createTime}}</h6>
          </div>
        </div>
      </div>
  </div>
</div>
