import {Component, Input, OnInit} from '@angular/core';
import {Account} from "../../../model/Account";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit
{
  @Input() user: any;
  path:string[] = [];
  location:string = '';

  ngOnInit(): void
  {
    var url = document.location.toString();
    var arrUrl = url.split("//");
    var start = arrUrl[1].indexOf("/");
    var relUrl = arrUrl[1].substring(start+1);
    this.path = relUrl.split("/");
    if(this.user.role == 'user')
    {
      this.location = 'MyTest';
    }
    else
    {
      this.location = 'Test Center'
    }
  }

  logout()
  {
    window.sessionStorage.removeItem("MCQuser");
    window.location.href = "login";
  }

}
<header>
  <div class="row navbar text-end menuMarginEnd border-bottom border-secondary-subtle">
    <div class="col-12 justify-content-end menuMarginTop">
      <a href="{{user.role + '/myAccount'}}">{{user.userName}}</a><span class="separator">|</span>
      <a href="{{user.role + '/resetPassword'}}">Change Password</a>
      <a class="login" (click)="logout()"> Logout</a>
    </div>
  </div>
  <div class="row logoRow">
    <div class="col-sm-6">
      <a class="logoMarginLeft" href="{{user.role + '/dashboard'}}">
        <img class="logoMarginTop" width="155" height="65" src="/assets/image/logo.png">
      </a>
    </div>
    <div class="col-sm-6 text-end">
      <h6 class="textMarginTop textMarginRight">{{location}}</h6>
    </div>
  </div>
</header>
<app-path [path] = "path">

</app-path>
