import {Component, OnInit} from '@angular/core';
import {Question} from "../../../model/Question";
import {QuestionService} from "../../../service/question-service";
import {Account} from "../../../model/Account";

@Component({
  selector: 'app-admin-manage-question-list-view',
  templateUrl: './adminManageQuestionListView.html',
  styleUrls: ['./adminManageQuestionListView.css']
})
export class AdminManageQuestionListView implements OnInit
{
  user:Account = new Account();
  questionList:Question[] = new Array();

  showAddQuestionView:boolean = false;
  showUpdateQuestionView:boolean = false;
  showNewQuestionView:boolean = false;
  showDeleteQuestionConfirmView:boolean = false;
  newQuestion:Question = new Question();
  currentQuestion:Question = new Question();
  choiceList:string[] = new Array();
  currentChoiceList:String[] = new Array();
  currentAnswerList:String[] = new Array();

  constructor(private questionService:QuestionService)
  {
  }

  ngOnInit(): void
  {
    // @ts-ignore
    this.user = JSON.parse( window.sessionStorage.getItem('MCQuser') );
    this.questionService.getAllQuestion()
      .subscribe(
        data=>
        {
          this.questionList = data;
          console.log(data);
        });
  }

  showAddQuestion()
  {
    this.showAddQuestionView = true;
  }

  closeAddQuestion()
  {
    this.showAddQuestionView = false;
  }

  getQuestionType()
  {
    this.newQuestion.style = (document.getElementById("newQuestionStyle") as HTMLSelectElement).value;
  }

  removeQuestionType()
  {
    this.newQuestion.style = '';
  }

  addChoice()
  {
    this.choiceList.push(this.choiceList.length.toString());
  }

  removeChoice(i:number)
  {
    this.choiceList.splice(i, 1);;
  }

  autoSaveChoice(i:number)
  {
    console.log(i);
    this.choiceList[i] = (document.getElementById('choice' + i) as HTMLInputElement).value;
    console.log(this.choiceList);
  }

  submit()
  {
    this.newQuestion.question = (document.getElementById("newQuestion") as HTMLTextAreaElement).value;
    this.newQuestion.point = parseInt((document.getElementById("newPoint") as HTMLInputElement).value);
    if(this.newQuestion.style == 'Short Answer')
    {
      this.newQuestion.body = ''
      this.newQuestion.answer = (document.getElementById("newAnswer") as HTMLTextAreaElement).value;
    }
    else
    {
      let answer:string[] = new Array();
      for(let i=0; i<this.choiceList.length; i++)
      {
        if((document.getElementById("selectedChoice" + i) as HTMLInputElement).checked == true)
        {
          answer.push(this.choiceList[i]);
        }
      }
      this.newQuestion.body = btoa(JSON.stringify(this.choiceList))
      this.newQuestion.answer = btoa(JSON.stringify(answer))
    }

    this.questionService.addNewQuestion(this.newQuestion)
      .subscribe(
        data=>
        {
          if (data != null)
          {
            //window.location.href = this.user.role + "/addTest/successful";
            this.currentQuestion = data;
            this.newQuestion = new Question();
            this.showAddQuestionView = false;
            this.showNewQuestionView = true;
            //window.open(this.user.role + "/test/" + this.currentTest.id);
            //window.location.href = this.user.role + "/addTest/successful";
          }
          else
          {
            window.location.href = this.user.role + "/addTest/failed";
          }
        },
        error =>
        {
          window.location.href = this.user.role + "/addQuestion/" + error.message;
        }
      )
  }

  closeNewQuestionView()
  {
    window.location.href = this.user.role + "/question";
  }

  QuestionView()
  {
    window.open(this.user.role + "/question/" + this.currentQuestion.id);
    window.location.href = this.user.role + "/question";
  }

  showQuestionView(q:Question)
  {
    window.open(this.user.role + "/question/" + q.id);
  }

  showUpdateQuestion(q:Question)
  {
    this.currentQuestion = q;
    if(q.style != 'Short Answer')
    {
      this.currentChoiceList = JSON.parse(atob(q.body));
      this.currentAnswerList = JSON.parse(atob(q.answer));
      console.log(this.currentAnswerList);
    }
    this.showUpdateQuestionView = true;
  }

  closeUpdateQuestion()
  {
    this.currentQuestion = new Question();
    this.showUpdateQuestionView = false;
  }

  showDeleteConfirm(q:Question)
  {
    this.currentQuestion = q;
    this.showDeleteQuestionConfirmView = true;
  }

  closeDeleteConfirm()
  {
    this.currentQuestion = new Question();
    this.showDeleteQuestionConfirmView = false;
  }


  addCurrentChoice()
  {
    this.currentChoiceList.push(this.choiceList.length.toString());
  }

  removeCurrentChoice(i:number)
  {
    this.currentChoiceList.splice(i, 1);;
  }

  isAnswer(index:number):boolean
  {
    for(let i =0; i < this.currentAnswerList.length; i++)
    {
      if(this.currentChoiceList[index] == this.currentAnswerList[i])
      {
        return true;
      }
    }
    return false;
  }

  autoSaveCurrentChoice(i:number)
  {
    this.currentChoiceList[i] = (document.getElementById('currentChoice' + i) as HTMLInputElement).value;
  }



}
<div class="col-12" *ngIf="showAddQuestionView==false && showUpdateQuestionView == false && showNewQuestionView == false && showDeleteQuestionConfirmView == false">
  <div class="row">
    <div class="col-3">
      <div class="card m-2">
        <div class="row m-2">
          <div class="col-10 text-end">
            <h3 style="text-align:center;margin:20px 0 5px;">Search Questions</h3>
          </div>
          <div class="col-2 text-start">
            <h3 style="text-align:center;margin:20px 0 5px; color:#4CAF50" (click)="showAddQuestion()"><i class="bi bi-plus-square"></i></h3>
          </div>
        </div>
        <div class="row m-2">
          <label><b>ID</b></label>
          <input class="form-control">
          <label><b>Question</b></label>
          <input class="form-control">
          <label><b>Type</b></label>
          <input class="form-control">
          <label><b>Topic</b></label>
          <input class="form-control">
          <label><b>Task Name</b></label>
          <input class="form-control">
        </div>
        <div class="row m-3 justify-content-end">
          <button type="button" >Search</button>
        </div>
      </div>
    </div>
    <div class="col-7">
      <div style="height:570px; overflow: auto">
        <div class="card m-2" style="width: 800px"  *ngFor="let question of questionList">
          <div class="row">
            <div class="col-2 ps-3 pt-2 border-end" style="width: 150px">
              <label class="h6">{{'#'+ question.id}}</label><br>
              <label class="h6">{{question.style}}</label><br>
              <label class="h6">{{"points: " + question.point}}</label>
            </div>
            <div class="col-7 text-start border-end" style="height: 100px; overflow: auto">
              <div class="m-2 h5">{{question.question}}</div>
            </div>
            <div class="col-2 ms-3 justify-content-end" style="width: 150px">
              <div class="row mt-2">
                <button class="form-control btn btn-sm btn-outline-primary pt-0" style="height:22px" (click)="showQuestionView(question)"  >VIEW  </button>
                <button class="form-control btn btn-sm btn-outline-success pt-0" style="height:22px" (click)="showUpdateQuestion(question)">UPDATE</button>
                <button class="form-control btn btn-sm btn-outline-danger  pt-0" style="height:22px" (click)="showDeleteConfirm(question)" >DELETE</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

<div class="col-12" *ngIf="showAddQuestionView==true">
  <div class="modal-content animate container mt-5" style="width:600px;height:250px" *ngIf="newQuestion.style==''">
    <div class="row">
      <div class="col-2 text-center">
        <button (click)="closeAddQuestion()">Back</button>
      </div>
    </div>
    <div class="row">
      <div class="col-12 text-center">
        <h2 style="text-align:center;margin:20px 0 5px;">Please Choose Question Type</h2>
        <select class="mt-2 text-center" id="newQuestionStyle" (change)="getQuestionType()">
          <option value="" >Choose Question Type</option>
          <option value="Single Choice"  >Single Choice</option>
          <option value="Multiple Choice">Multiple Choice</option>
          <option value="Short Answer"   >Short Answer</option>
        </select>
      </div>
    </div>
  </div>
  <div class="modal-content animate container mt-3 mb-5" style="width:750px;min-height:470px" *ngIf="newQuestion.style!=''">
    <div class="row">
      <div class="col-2 text-center">
        <button (click)="removeQuestionType()">Back</button>
      </div>
      <div class="col-8 text-center">
        <h2 style="text-align:center;margin:20px 0 5px;">{{ 'Create ' + newQuestion.style + ' Question'}}</h2>
      </div>
    </div>

    <div class="row" *ngIf="newQuestion.style=='Short Answer'">
      <label><b>Question</b></label>
      <textarea type="text"  id="newQuestion">{{newQuestion.question}}</textarea>
      <label><b>Answer</b></label>
      <textarea type="text"  id="newAnswer">{{newQuestion.answer}}</textarea>
      <label><b>Point</b></label>
      <input type="number"  id="newPoint"  value="{{newQuestion.point}}"  required>
    </div>

    <div class="row" *ngIf="newQuestion.style=='Single Choice'">
      <label><b>Question</b></label>
      <textarea type="text"  id="newQuestion">{{newQuestion.question}}</textarea>
      <div class="row" style="min-height: 200px">
        <div class="col-6 border-end">
          <div class="row border-bottom">
            <label><i class="bi bi-plus-square" (click)="addChoice()"><b> Choice</b></i></label>
          </div>
          <span *ngFor="let c of choiceList; let i = index" >
            <input class="me-1" id="{{'choice'+i}}" type="text" style="width:90%;height:20px" value="{{c}}" (change)="autoSaveChoice(i)"><i class="bi bi-dash-square" (click)="removeChoice(i)"></i>
          </span>

        </div>
        <div class="col-6">
          <div class="row border-bottom">
            <label><b>Answer</b></label>
          </div>
          <div class="mt-1" *ngFor="let c of choiceList; let i = index">
            <input class="me-1" type="radio" name="answer" value="{{c}}" id="{{'selectedChoice' + i}}"><label>{{c}}</label>
          </div>
        </div>
      </div>
      <label><b>Point</b></label>
      <input type="number"  id="newPoint"  value="{{newQuestion.point}}"  required>
    </div>

    <div class="row" *ngIf="newQuestion.style=='Multiple Choice'">
      <label><b>Question</b></label>
      <textarea type="text"  id="newQuestion">{{newQuestion.question}}</textarea>
      <div class="row" style="min-height: 200px">
        <div class="col-6 border-end">
          <div class="row border-bottom">
            <label><i class="bi bi-plus-square" (click)="addChoice()"><b> Choice</b></i></label>
          </div>
          <span *ngFor="let c of choiceList; let i = index" >
            <input class="me-1" id="{{'choice'+i}}" type="text" style="width:90%;height:20px" value="{{c}}" (change)="autoSaveChoice(i)"><i class="bi bi-dash-square" (click)="removeChoice(i)"></i>
          </span>

        </div>
        <div class="col-6">
          <div class="row border-bottom">
            <label><b>Answer</b></label>
          </div>
          <div class="mt-1" *ngFor="let c of choiceList; let i = index">
            <input class="me-1" type="checkbox" name="answer" value="{{c}}" id="{{'selectedChoice' + i}}"><label>{{c}}</label>
          </div>
        </div>
      </div>
      <label><b>Point</b></label>
      <input type="number"  id="newPoint"  value="{{newQuestion.point}}"  required>
    </div>

    <div class="row justify-content-end">
      <div class="col-3">
        <button type="button" (click)="submit()">Submit</button>
      </div>
    </div>
  </div>
</div>

<div class="row" *ngIf="showNewQuestionView == true">
  <div class="modal-content container mt-2" style="width: 600px">
    <div class="row text-center m-4">
      <label class="h3"><b>Create Question Successful</b></label>
      <label class="h3"><b>Do You Want to Add This Question to A Test?</b></label>
    </div>
    <div class="row">
      <div class="col-6">
        <button type="button" (click)="QuestionView()">Yes</button>
      </div>
      <div class="col-6">
        <button type="button" style="background-color: #C44336" (click)="closeNewQuestionView()">No</button>
      </div>
    </div>
  </div>
</div>

<div class="row" *ngIf="showUpdateQuestionView == true">
  <div class="modal-content animate container mt-3 mb-3" style="width:600px;min-height:470px">
    <div class="row">
      <div class="col-2 text-center">
        <button (click)="closeUpdateQuestion()">Back</button>
      </div>
    </div>
    <div class="row" *ngIf="currentQuestion.style=='Short Answer'">
      <label><b>Question</b></label>
      <textarea type="text"  id="currentQuestion">{{currentQuestion.question}}</textarea>
      <label><b>Answer</b></label>
      <textarea type="text"  id="currentAnswer">{{currentQuestion.answer}}</textarea>
      <label><b>Point</b></label>
      <input type="number"  id="currentPoint"  value="{{currentQuestion.point}}"  required>
    </div>

    <div class="row" *ngIf="currentQuestion.style=='Single Choice'">
      <label><b>Question</b></label>
      <textarea type="text"  id="currentQuestion">{{currentQuestion.question}}</textarea>
      <div class="row" style="min-height: 200px">
        <div class="col-6 border-end">
          <div class="row border-bottom">
            <label><i class="bi bi-plus-square" (click)="addCurrentChoice()"><b> Choice</b></i></label>
          </div>
          <span *ngFor="let c of currentChoiceList; let i = index" >
            <input class="me-1" id="{{'currentChoice'+i}}" type="text" style="width:90%;height:20px" value="{{c}}" (change)="autoSaveCurrentChoice(i)"><i class="bi bi-dash-square" (click)="removeCurrentChoice(i)"></i>
          </span>

        </div>
        <div class="col-6">
          <div class="row border-bottom">
            <label><b>Answer</b></label>
          </div>
          <div class="mt-1" *ngFor="let c of currentChoiceList; let i = index">
            <input class="me-1" type="radio" name="answer" value="{{c}}" id="{{'selectedCurrentChoice' + i}}" *ngIf="isAnswer(i)" checked><label *ngIf="isAnswer(i)==true" >{{c}}</label>
            <input class="me-1" type="radio" name="answer" value="{{c}}" id="{{'selectedCurrentChoice' + i}}" *ngIf="isAnswer(i)==false" ><label *ngIf="isAnswer(i)==false">{{c}}</label>
          </div>
        </div>
      </div>
      <label><b>Point</b></label>
      <input type="number"  id="currentPoint"  value="{{currentQuestion.point}}"  required>
    </div>

    <div class="row" *ngIf="currentQuestion.style=='Multiple Choice'">
      <label><b>Question</b></label>
      <textarea type="text"  id="currentQuestion">{{currentQuestion.question}}</textarea>
      <div class="row" style="min-height: 200px">
        <div class="col-6 border-end">
          <div class="row border-bottom">
            <label><i class="bi bi-plus-square" (click)="addCurrentChoice()"><b> Choice</b></i></label>
          </div>
          <span *ngFor="let c of currentChoiceList; let i = index" >
            <input class="me-1" id="{{'currentChoice'+i}}" type="text" style="width:90%;height:20px" value="{{c}}" (change)="autoSaveCurrentChoice(i)"><i class="bi bi-dash-square" (click)="removeCurrentChoice(i)"></i>
          </span>
        </div>
        <div class="col-6">
          <div class="row border-bottom">
            <label><b>Answer</b></label>
          </div>
          <div class="mt-1" *ngFor="let c of currentChoiceList; let i = index">
            <input class="me-1" type="checkbox" name="answer" value="{{c}}" id="{{'selectedCurrentChoice' + i}}" *ngIf="isAnswer(i)" checked><label *ngIf="isAnswer(i)==true" >{{c}}</label>
            <input class="me-1" type="checkbox" name="answer" value="{{c}}" id="{{'selectedCurrentChoice' + i}}" *ngIf="isAnswer(i)==false" ><label *ngIf="isAnswer(i)==false">{{c}}</label>
          </div>
        </div>
      </div>
      <label><b>Point</b></label>
      <input type="number"  id="currentPoint"  value="{{currentQuestion.point}}"  required>
    </div>

    <div class="row justify-content-end">
      <div class="col-3">
        <button type="button" (click)="submit()">Submit</button>
      </div>
    </div>
  </div>
</div>

<div class="row" *ngIf="showDeleteQuestionConfirmView == true">
  <div class="modal-content animate container mt-2" style="width: 600px">
      <div class="row m-1">
        <h4>Are You Sure To delete This Question?</h4>
        <hr>
        <label style="font-size: 5px">{{"#" + currentQuestion.id}}</label>
        <label style="font-size: 5px">{{currentQuestion.question}}</label>
        <hr>
      </div>
      <div class="row ms-1 me-1">
        <div class="col-6">
          <button type="button">Yes</button>
        </div>
        <div class="col-6">
          <button type="button" style="background-color: #C44336" (click)="closeDeleteConfirm()">No</button>
        </div>
      </div>
  </div>
</div>
